import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import tutorialsdev from '../../assets/tutorialsdev.png'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
	const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
	const [conversations, setConversations] = useState([])
	const [messages, setMessages] = useState({})
	const [message, setMessage] = useState('')
	const [users, setUsers] = useState([])
	const [socket, setSocket] = useState(null)
	const messageRef = useRef(null)

	useEffect(() => {
		setSocket(io('http://localhost:8080'))
	}, [])

	useEffect(() => {
		socket?.emit('addUser', user?.id);
		socket?.on('getUsers', users => {
			console.log('activeUsers :>> ', users);
		})
		socket?.on('getMessage', data => {
			setMessages(prev => ({
				...prev,
				messages: [...prev.messages, { user: data.user, message: data.message }]
			}))
		})
	}, [socket])

	useEffect(() => {
		messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
	}, [messages?.messages])

	useEffect(() => {
		const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
		const fetchConversations = async () => {
			const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setConversations(resData)
		}
		fetchConversations()
	}, [])

	useEffect(() => {
		const fetchUsers = async () => {
			const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setUsers(resData)
		}
		fetchUsers()
	}, [])

	const fetchMessages = async (conversationId, receiver) => {
		const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
			}
		});
		const resData = await res.json()
		setMessages({ messages: resData, receiver, conversationId })
	}

	const sendMessage = async (e) => {
		setMessage('')
		socket?.emit('sendMessage', {
			senderId: user?.id,
			receiverId: messages?.receiver?.receiverId,
			message,
			conversationId: messages?.conversationId
		});
		const res = await fetch(`http://localhost:8000/api/message`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				conversationId: messages?.conversationId,
				senderId: user?.id,
				message,
				receiverId: messages?.receiver?.receiverId
			})
		});
	}

	return (
		<div className=" w-screen flex">
      {/* Converstaion Part */}
      <div className=" w-[25%] h-screen bg-secondary overflow-scroll no-scrollbar">
        <div className=" flex justify-center items-center my-4">
          <div className="border border-white p-[2px] rounded-full">
            <img src={Img1} alt="" width={75} height={75} />
          </div>
          <div className="ml-4">
            <h3 className=" text-2xl">{user?.fullName}</h3>
            <p className=" text-lg font-light">My Account</p>
          </div>
        </div>
        <hr />
        <div className="px-4 pt-1 bg-light">
          <div className="text-black text-lg">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user }) => {
                return (
                  <div
                    className="flex items-center py-3 border-b border-b-gray-300"
                    onClick={() => fetchMessages(conversationId, user)}
                  >
                    <div className="cursor-pointer flex items-center">
                      <div>
                        <img
                          src={Img1}
                          alt="Img1"
                          className="w-[60px] h-[60px] rounded-full p-[2px]"
                        />
                      </div>
                      <div className="ml-3">
                        <h3 className="text-lg font-semibold">
                          {user?.fullName}
                        </h3>
                        <p className="text-sm font-light text-gray-600">
                          {user?.email}
                        </p>
                      </div>
                    </div>
                  </div>
                );
              })
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No Conversations
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Message Part */}
      <div className=" w-[50%] h-screen bg-white flex flex-col items-center no-scrollbar">
        {messages?.reciever?.fullName && (
          <div className="w-[85%] bg-secondary h-[80px] my-3 rounded-full flex items-center px-10">
            <div className="cursor-pointer">
              <img
                src={Img1}
                alt="Img1"
                width={60}
                height={60}
                className="rounded-full"
              />
            </div>
            <div className="ml-4 mr-auto">
              <h3 className="text-lg">{messages?.reciever?.fullName}</h3>
              <p className="text-sm font-light text-gray-600">
                {messages?.reciever?.email}
              </p>
            </div>
            <div className="cursor-pointer">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="icon icon-tabler icon-tabler-phone-outgoing"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke="black"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <path d="M5 4h4l2 5l-2.5 1.5a11 11 0 0 0 5 5l1.5 -2.5l5 2v4a2 2 0 0 1 -2 2a16 16 0 0 1 -15 -15a2 2 0 0 1 2 -2" />
                <line x1="15" y1="9" x2="20" y2="4" />
                <polyline points="16 4 20 4 20 8" />
              </svg>
            </div>
          </div>
        )}
        <div className=" h-[80%] bg-chat  border-t border-b w-full mt-[19px] overflow-scroll no-scrollbar">
          <div className="p-14 mt-[-4%]">
            {messages?.messages?.length > 0 ? (
              messages?.messages?.map(({ message, user: { id } = {} }) => {
                return (
                  <div
                    className={`max-w-[45%] rounded-b-xl p-4 mb-6 ${
                      id === user?.id
                        ? "bg-primary text-white rounded-tl-xl ml-auto"
                        : "bg-white rounded-tr-xl"
                    } `}
                  >
                    {message}
                  </div>
                );
              })
            ) : (
              <div className=" text-center text-lg font-semibold mt-24">
                {" "}
                No Messages or Conversation selected
              </div>
            )}
          </div>
        </div>
        {messages?.reciever?.fullName && (
          <div className="p-8 w-full flex items-center">
            <Input
              placeholder="Type a message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="w-[85%]"
              inputClassName="p-4 border-0 shadow-md rounded-full bg-light focus:ring-0 focus:border-0 outline-none"
            />
            <div
              className={`ml-4 mt-3 p-2 cursor-pointer bg-light rounded-full ${
                !message && "pointer-events-none"
              }`}
              onClick={() => sendMessage()}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="icon icon-tabler icon-tabler-send"
                width="30"
                height="30"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke="#2c3e50"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <path d="M10 14l11 -11" />
                <path d="M21 3l-6.5 18a.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a.55 .55 0 0 1 0 -1l18 -6.5" />
              </svg>
            </div>
            <div
              className={`ml-4 mt-3 p-2 cursor-pointer bg-light rounded-full ${
                !message && "pointer-events-none"
              }`}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="icon icon-tabler icon-tabler-circle-plus"
                width="30"
                height="30"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke="#2c3e50"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <circle cx="12" cy="12" r="9" />
                <line x1="9" y1="12" x2="15" y2="12" />
                <line x1="12" y1="9" x2="12" y2="15" />
              </svg>
            </div>
          </div>
        )}
      </div>

      {/* Peoples */}
      <div className=" w-[25%] h-screen bg-light px-8 py-8 overflow-scroll no-scrollbar">
        <div className="text-[#071952] text-lg mb-5">Peoples</div>
        <div>
          {users.length > 0 ? (
            users.map(({ userId, user }) => {
              return (
                <div
                  className="flex items-center py-3 border-b border-b-gray-300"
                  onClick={() => fetchMessages("new", user)}
                >
                  <div className="cursor-pointer flex items-center">
                    <div>
                      <img
                        src={Img1}
                        alt="Img1"
                        className="w-[60px] h-[60px] rounded-full p-[2px]"
                      />
                    </div>
                    <div className="ml-3">
                      <h3 className="text-lg font-semibold">
                        {user?.fullName}
                      </h3>
                      <p className="text-sm font-light text-gray-600">
                        {user?.email}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="text-center text-lg font-semibold mt-24">
              No Conversations
            </div>
          )}
        </div>
      </div>
    </div>
	)
}

export default Dashboard
